// Вертикальный скролл

jQuery(function ($) {
	$.fn.hScroll = function (amount) {
			amount = amount || 80;
			$(this).bind("DOMMouseScroll mousewheel resize", function (event) {
					var oEvent = event.originalEvent, 
							direction = oEvent.detail ? oEvent.detail * -amount : oEvent.wheelDelta, 
							position = $(this).scrollLeft();
					position += direction > 0 ? -amount : amount;
					$(this).scrollLeft(position);
					event.preventDefault();
			})
	};
	$.fn.hScrollDestroy = function (amount) {
			
			$(this).off("DOMMouseScroll mousewheel")
	};
});


$(document).ready(function() {
	var scrollInit = false;

	function initScroll() {
					if ($(window).width() > 1199 && !scrollInit) {
							if (scrollInit == false) {
								
								$('#main-scroll').hScroll(80);
							}
							scrollInit = true;
					} else {
						$('#main-scroll').hScrollDestroy();
						scrollInit = false;
					}
	}
	initScroll();
	
	$(window).on('resize', initScroll);
});




