// Menu

$(document).ready(function() {
	var mmenuInit = false;

	function mmenuClass() {
		if ($(window).width() < 1199) {
					if (mmenuInit == false) {
						$(".c-header__left").click(function() {
							$('.c-header__mmenu').toggleClass('active');
						});
						$(document).mouseup(function (e){ // событие клика по веб-документу
							var div = $(".c-header__left"); // тут указываем ID элемента
							if (!div.is(e.target) // если клик был не по нашему блоку
									&& div.has(e.target).length === 0) { // и не по его дочерним элементам
										$('.c-header__mmenu').removeClass('active');
							}
						});
					}
					mmenuInit = true;
			} else {
				$(".c-header__left").off(function() {
					$('.c-header__mmenu').off('active');
				});
					mmenuInit = false;
			}
		}
		mmenuClass();
    $(window).on('resize', mmenuClass);
})

